import classes from './MealSummary.module.css'

const MealSummary=()=>{
    return(
        <section className={classes.summary}>
            <h2>Welcome to FOODIE</h2>
            <p className='p1'>In the realm of delicious food filled with lot of flavours like sweet, spicy and diversity in taste , why not take 
                opportunity to create good memory by enjoying a moment with us that transcends the ordinary and provide a 
                 sensorial adventure.</p>
            <p className='p2'>"In this culinary haven, each dish tells a story, and tonight, why not you come and 
                savor a chapter of delight with us".
            </p>
        </section>
    )

}
export default MealSummary;
